    input [3:0] slot_size;
    input [1:0] fec_sel; // 0..3
    reg   [9:0] coeff;
begin
    case (fec_sel)
        2'd0: coeff = 10'd192;
        2'd1: coeff = 10'd128;
        2'd2: coeff = 10'd96;
        2'd3: coeff = 10'd64;
        default: coeff = 10'd0;
