    end else begin
      
           // SEND_LDPC
      s_axis_tready <= 1'b0;
      m_axis_tvalid <= 1'b1;

      // ƒержим выходные данные и TLAST стабильными, даже если m_axis_tready=0
      m_axis_tdata <= acc[2879 - send_bits];
      m_axis_tlast <= (send_bits == length_ldpc_v(key_q[3:0], key_q[5:4]) - 1);

      // —чЄтчик и переходы - только по рукопожатию
      if (m_axis_tready) begin
        if (m_axis_tlast) begin
          // ѕоследний бит прин€т - возвращаемс€ к приЄму данных
          state         <= RECV_DATA;
          send_bits     <= 0;
          acc           <= 0;
          shift         <= 0;
          shift_cnt     <= 0;
          new_row       <= 1'b1;
          row           <= 0;
          cfg_ready     <= 1'b1;
          m_axis_tvalid <= 1'b0; // можно сразу опустить valid
          // m_axis_tlast  <= 1'b0; 
        end else begin
          send_bits <= send_bits + 1'b1;
        end
      end

    end
  end
end
endmodule